  private char unhex(int length) {
    int number = 0;
    for (int ix = 0; ix < length; ix++) {
      int digit;
      char ch = yycharat(2 + ix);
      if (ch >= '0' && ch <= '9')
        digit = ch - '0';
      else if (ch >= 'a' && ch <= 'f')
        digit = (ch - 'a') + 10;
      else if (ch >= 'A' && ch <= 'F')
        digit = (ch - 'A') + 10;
      else
        throw new InvalidTopicMapException("Invalid escape sequence: '" +
                                           yytext() + "'");
    
      number = number * 16 + digit;
    }
    return (char) number;
  }  


