  /** 
   * Unpacks the compressed character translation table.
   *
   * @param packed   the packed character translation table
   * @return         the unpacked character translation table
   */
  private static char [] zzUnpackCMap(String packed) {
    char [] map = new char[0x10000];
    int i = 0;  /* index in packed string  */
    int j = 0;  /* index in unpacked array */
    while (i < 142) {
      int  count = packed.charAt(i++);
      char value = packed.charAt(i++);
      do map[j++] = value; while (--count > 0);
    }
    return map;
  }


  /**
   * Refills the input buffer.
   *
   * @return      <code>false</code>, iff there was new input.
   * 
   * @exception   java.io.IOException  if any I/O-Error occurs
   */
  private boolean zzRefill() throws java.io.IOException {

    /* first: make room (if you can) */
    if (zzStartRead > 0) {
      System.arraycopy(zzBuffer, zzStartRead,
                       zzBuffer, 0,
                       zzEndRead-zzStartRead);

      /* translate stored positions */
      zzEndRead-= zzStartRead;
      zzCurrentPos-= zzStartRead;
      zzMarkedPos-= zzStartRead;
      zzStartRead = 0;
    }

    /* is the buffer big enough? */
    if (zzCurrentPos >= zzBuffer.length) {
      /* if not: blow it up */
      char newBuffer[] = new char[zzCurrentPos*2];
      System.arraycopy(zzBuffer, 0, newBuffer, 0, zzBuffer.length);
      zzBuffer = newBuffer;
    }

    /* finally: fill the buffer with new input */
    int numRead = zzReader.read(zzBuffer, zzEndRead,
                                            zzBuffer.length-zzEndRead);

    if (numRead > 0) {
      zzEndRead+= numRead;
      return false;
    }
    // unlikely but not impossible: read 0 characters, but not at end of stream    
    if (numRead == 0) {
      int c = zzReader.read();
      if (c == -1) {
        return true;
      } else {
        zzBuffer[zzEndRead++] = (char) c;
        return false;
      }     
    }

	// numRead < 0
    return true;
  }

    
  /**
   * Closes the input stream.
   */
  public final void yyclose() throws java.io.IOException {
    zzAtEOF = true;            /* indicate end of file */
    zzEndRead = zzStartRead;  /* invalidate buffer    */

    if (zzReader != null)
      zzReader.close();
  }


  /**
   * Resets the scanner to read from a new input stream.
   * Does not close the old reader.
   *
   * All internal variables are reset, the old input stream 
   * <b>cannot</b> be reused (internal buffer is discarded and lost).
   * Lexical state is set to <tt>ZZ_INITIAL</tt>.
   *
   * @param reader   the new input stream 
   */
  public final void yyreset(java.io.Reader reader) {
    zzReader = reader;
    zzAtBOL  = true;
    zzAtEOF  = false;
    zzEOFDone = false;
    zzEndRead = zzStartRead = 0;
    zzCurrentPos = zzMarkedPos = 0;
    yyline = yychar = yycolumn = 0;
    zzLexicalState = YYINITIAL;
  }


  /**
   * Returns the current lexical state.
   */
  public final int yystate() {
    return zzLexicalState;
  }


  /**
   * Enters a new lexical state
   *
   * @param newState the new lexical state
   */
  public final void yybegin(int newState) {
    zzLexicalState = newState;
  }


  /**
   * Returns the text matched by the current regular expression.
   */
  public final String yytext() {
    return new String( zzBuffer, zzStartRead, zzMarkedPos-zzStartRead );
  }


  /**
   * Returns the character at position <tt>pos</tt> from the 
   * matched text. 
   * 
   * It is equivalent to yytext().charAt(pos), but faster
   *
   * @param pos the position of the character to fetch. 
   *            A value from 0 to yylength()-1.
   *
   * @return the character at position pos
   */
  public final char yycharat(int pos) {
    return zzBuffer[zzStartRead+pos];
  }


  /**
   * Returns the length of the matched text region.
   */
  public final int yylength() {
    return zzMarkedPos-zzStartRead;
  }


  /**
   * Reports an error that occured while scanning.
   *
   * In a wellformed scanner (no or only correct usage of 
   * yypushback(int) and a match-all fallback rule) this method 
   * will only be called with things that "Can't Possibly Happen".
   * If this method is called, something is seriously wrong
   * (e.g. a JFlex bug producing a faulty scanner etc.).
   *
   * Usual syntax/scanner level error handling should be done
   * in error fallback rules.
   *
   * @param   errorCode  the code of the errormessage to display
   */
  private void zzScanError(int errorCode) {
    String message;
    try {
      message = ZZ_ERROR_MSG[errorCode];
    }
    catch (ArrayIndexOutOfBoundsException e) {
      message = ZZ_ERROR_MSG[ZZ_UNKNOWN_ERROR];
    }

    throw new Error(message);
  } 


  /**
   * Pushes the specified amount of characters back into the input stream.
   *
   * They will be read again by then next call of the scanning method
   *
   * @param number  the number of characters to be read again.
   *                This number must not be greater than yylength()!
   */
  public void yypushback(int number)  {
    if ( number > yylength() )
      zzScanError(ZZ_PUSHBACK_2BIG);

    zzMarkedPos -= number;
  }


  /**
   * Resumes scanning until the next regular expression is matched,
   * the end of input is encountered or an I/O-Error occurs.
   *
   * @return      the next token
   * @exception   java.io.IOException  if any I/O-Error occurs
   */
  public Token nextToken2() throws java.io.IOException {
    int zzInput;
    int zzAction;

    // cached fields:
    int zzCurrentPosL;
    int zzMarkedPosL;
    int zzEndReadL = zzEndRead;
    char [] zzBufferL = zzBuffer;
    char [] zzCMapL = ZZ_CMAP;

    int [] zzTransL = ZZ_TRANS;
    int [] zzRowMapL = ZZ_ROWMAP;
    int [] zzAttrL = ZZ_ATTRIBUTE;

    while (true) {
      zzMarkedPosL = zzMarkedPos;

      boolean zzR = false;
      for (zzCurrentPosL = zzStartRead; zzCurrentPosL < zzMarkedPosL;
                                                             zzCurrentPosL++) {
        switch (zzBufferL[zzCurrentPosL]) {
        case '\u000B':
        case '\u000C':
        case '\u0085':
        case '\u2028':
        case '\u2029':
          yyline++;
          yycolumn = 0;
          zzR = false;
          break;
        case '\r':
          yyline++;
          yycolumn = 0;
          zzR = true;
          break;
        case '\n':
          if (zzR)
            zzR = false;
          else {
            yyline++;
            yycolumn = 0;
          }
          break;
        default:
          zzR = false;
          yycolumn++;
        }
      }

      if (zzR) {
        // peek one character ahead if it is \n (if we have counted one line too much)
        boolean zzPeek;
        if (zzMarkedPosL < zzEndReadL)
          zzPeek = zzBufferL[zzMarkedPosL] == '\n';
        else if (zzAtEOF)
          zzPeek = false;
        else {
          boolean eof = zzRefill();
          zzEndReadL = zzEndRead;
          zzMarkedPosL = zzMarkedPos;
          zzBufferL = zzBuffer;
          if (eof) 
            zzPeek = false;
          else 
            zzPeek = zzBufferL[zzMarkedPosL] == '\n';
        }
        if (zzPeek) yyline--;
      }
      zzAction = -1;

      zzCurrentPosL = zzCurrentPos = zzStartRead = zzMarkedPosL;
  
      zzState = ZZ_LEXSTATE[zzLexicalState];


      zzForAction: {
        while (true) {
    
          if (zzCurrentPosL < zzEndReadL)
            zzInput = zzBufferL[zzCurrentPosL++];
          else if (zzAtEOF) {
            zzInput = YYEOF;
            break zzForAction;
          }
          else {
            // store back cached positions
            zzCurrentPos  = zzCurrentPosL;
            zzMarkedPos   = zzMarkedPosL;
            boolean eof = zzRefill();
            // get translated positions and possibly new buffer
            zzCurrentPosL  = zzCurrentPos;
            zzMarkedPosL   = zzMarkedPos;
            zzBufferL      = zzBuffer;
            zzEndReadL     = zzEndRead;
            if (eof) {
              zzInput = YYEOF;
              break zzForAction;
            }
            else {
              zzInput = zzBufferL[zzCurrentPosL++];
            }
          }
          int zzNext = zzTransL[ zzRowMapL[zzState] + zzCMapL[zzInput] ];
          if (zzNext == -1) break zzForAction;
          zzState = zzNext;

          int zzAttributes = zzAttrL[zzState];
          if ( (zzAttributes & 1) == 1 ) {
            zzAction = zzState;
            zzMarkedPosL = zzCurrentPosL;
            if ( (zzAttributes & 8) == 8 ) break zzForAction;
          }

        }
      }

      // store back cached position
      zzMarkedPos = zzMarkedPosL;

      switch (zzAction < 0 ? zzAction : ZZ_ACTION[zzAction]) {
        case 41: 
          { string.append(unhex(4));
          }
        case 50: break;
        case 2: 
          { /* ignore */
          }
        case 51: break;
        case 4: 
          { return newToken(CTMParser.RIGHTPAREN);
          }
        case 52: break;
        case 7: 
          { return newToken(CTMParser.STOP);
          }
        case 53: break;
        case 34: 
          { return newToken(CTMParser.ONEOH);
          }
        case 54: break;
        case 27: 
          { string.append((char) 10);
          }
        case 55: break;
        case 3: 
          { return newToken(CTMParser.LEFTPAREN);
          }
        case 56: break;
        case 18: 
          { return newToken(CTMParser.LEFTBRACKET);
          }
        case 57: break;
        case 16: 
          { return newToken(CTMParser.EQUALS);
          }
        case 58: break;
        case 37: 
          { return newToken(CTMParser.ISA);
          }
        case 59: break;
        case 43: 
          { return newToken(CTMParser.INCLUDE);
          }
        case 60: break;
        case 36: 
          { return newToken(CTMParser.DEF);
          }
        case 61: break;
        case 10: 
          { return newToken(CTMParser.SEMICOLON);
          }
        case 62: break;
        case 22: 
          { yybegin(YYINITIAL);
       return newToken(CTMParser.SINGLE_QUOTED_STRING, string.toString());
          }
        case 63: break;
        case 24: 
          { return newToken(CTMParser.HATHAT);
          }
        case 64: break;
        case 40: 
          { return newToken(CTMParser.TRIPLE_QUOTED_STRING,
                                     yytext().substring(3, yylength() - 3));
          }
        case 65: break;
        case 12: 
          { string.setLength(0); // empty string buffer
                     yybegin(STRING);
          }
        case 66: break;
        case 26: 
          { string.append('"');
          }
        case 67: break;
        case 29: 
          { string.append('\\');
          }
        case 68: break;
        case 6: 
          { return newToken(CTMParser.INTEGER);
          }
        case 69: break;
        case 14: 
          { return newToken(CTMParser.COMMA);
          }
        case 70: break;
        case 44: 
          { return newToken(CTMParser.VERSION);
          }
        case 71: break;
        case 38: 
          { return newToken(CTMParser.AKO);
          }
        case 72: break;
        case 17: 
          { return newToken(CTMParser.TILDE);
          }
        case 73: break;
        case 13: 
          { return newToken(CTMParser.INFINITY);
          }
        case 74: break;
        case 21: 
          { string.append(yycharat(0));
          }
        case 75: break;
        case 42: 
          { return newToken(CTMParser.PREFIX);
          }
        case 76: break;
        case 49: 
          { return newToken(CTMParser.DATETIME);
          }
        case 77: break;
        case 11: 
          { return newToken(CTMParser.RIGHTBRACKET);
          }
        case 78: break;
        case 9: 
          { return newToken(CTMParser.HYPHEN);
          }
        case 79: break;
        case 33: 
          { return newToken(CTMParser.WRAPPED_IRI,
                                     yytext().substring(1, yylength() - 1));
          }
        case 80: break;
        case 8: 
          { return newToken(CTMParser.IDENTIFIER);
          }
        case 81: break;
        case 32: 
          { return newToken(CTMParser.QNAME);
          }
        case 82: break;
        case 28: 
          { string.append((char) 13);
          }
        case 83: break;
        case 39: 
          { return newToken(CTMParser.IRI);
          }
        case 84: break;
        case 48: 
          { return newToken(CTMParser.DATE);
          }
        case 85: break;
        case 1: 
          { throw new InvalidTopicMapException("Illegal character <"+yytext()+"> at " +
                                     docuri + ":" + (yyline+1) + ":" + yycolumn);
          }
        case 86: break;
        case 19: 
          { return newToken(CTMParser.HAT);
          }
        case 87: break;
        case 25: 
          { return newToken(CTMParser.NAMED_WILDCARD,
                                     yytext().substring(1));
          }
        case 88: break;
        case 45: 
          { string.append(unhex(6));
          }
        case 89: break;
        case 35: 
          { return newToken(CTMParser.END);
          }
        case 90: break;
        case 15: 
          { return newToken(CTMParser.AT);
          }
        case 91: break;
        case 23: 
          { return newToken(CTMParser.VARIABLE);
          }
        case 92: break;
        case 30: 
          { string.append((char) 9);
          }
        case 93: break;
        case 20: 
          { return newToken(CTMParser.WILDCARD);
          }
        case 94: break;
        case 47: 
          { return newToken(CTMParser.MERGEMAP);
          }
        case 95: break;
        case 46: 
          { return newToken(CTMParser.ENCODING);
          }
        case 96: break;
        case 31: 
          { return newToken(CTMParser.DECIMAL);
          }
        case 97: break;
        case 5: 
          { return newToken(CTMParser.COLON);
          }
        case 98: break;
        default: 
          if (zzInput == YYEOF && zzStartRead == zzCurrentPos) {
            zzAtEOF = true;
              {
                return newToken(CTMParser.EOF);
              }
          } 
          else {
            zzScanError(ZZ_NO_MATCH);
          }
      }
    }
  }


